import React, { useState } from "react";


function Form(props) {
  const [note, setNote] = useState({
    Username: "",
    Password: ""
  });

  function handleChange(event) {
    const { name, value } = event.target;

    setNote(prevNote => {
      return {
        ...prevNote,
        [name]: value
      };
    });
  }

  function submitNote(event) {
    props.onAdd(note);
    setNote({
      title: "",
      content: ""
    });
    event.preventDefault();
  }
  return (


    <form className="form">
      <input name="Username"  value={note.Username}  onChange={handleChange} type="text" placeholder="Username" />
      <input name= "password" value={note.Password}  onChange={handleChange} type="password" placeholder="Password" />
      {!props.isRegistered && (
        <input type="password" placeholder="Confirm Password" />
      )}

      <button type="submit">{props.isRegistered ? "Login" : "Register"}</button>
    </form>
  );
}

export default Form;
